import React, { useState, useRef, useEffect } from 'react';
import { gsap } from 'gsap';

const BubblePage = () => {
  const [inputValue, setInputValue] = useState('');
  const [bubbles, setBubbles] = useState([]);
  const [nextId, setNextId] = useState(1);
  const containerRef = useRef(null);
  const inputRef = useRef(null);

  // Array of bubble colors - minimal black and white theme
  const bubbleColors = [
    '#000000', '#1a1a1a', '#333333', '#4d4d4d', '#666666',
    '#808080', '#999999', '#b3b3b3', '#cccccc', '#ffffff'
  ];

  const handleSubmit = (e) => {
    e.preventDefault();
    if (inputValue.trim()) {
      createBubble(inputValue.trim());
      setInputValue('');
    }
  };

  const createBubble = (text) => {
    const newBubble = {
      id: nextId,
      text,
      color: bubbleColors[Math.floor(Math.random() * bubbleColors.length)],
      x: Math.random() * 70 + 15, // Random position (15% to 85% from left)
      y: Math.random() * 60 + 20, // Random position (20% to 80% from top)
      size: Math.min(text.length * 8 + 40, 120), // Size based on text length, max 120px
    };

    setBubbles(prev => [...prev, newBubble]);
    setNextId(prev => prev + 1);

    // Animate bubble creation
    setTimeout(() => {
      const bubbleElement = document.getElementById(`bubble-${newBubble.id}`);
      if (bubbleElement) {
        gsap.fromTo(bubbleElement, 
          { 
            scale: 0,
            rotation: -180,
            opacity: 0
          },
          { 
            scale: 1,
            rotation: 0,
            opacity: 1,
            duration: 0.6,
            ease: "back.out(1.7)"
          }
        );
      }
    }, 50);
  };

  const popBubble = (bubbleId) => {
    const bubbleElement = document.getElementById(`bubble-${bubbleId}`);
    if (bubbleElement) {
      // Pop animation
      gsap.to(bubbleElement, {
        scale: 1.3,
        duration: 0.1,
        ease: "power2.out",
        onComplete: () => {
          gsap.to(bubbleElement, {
            scale: 0,
            opacity: 0,
            rotation: 360,
            duration: 0.3,
            ease: "power2.in",
            onComplete: () => {
              setBubbles(prev => prev.filter(bubble => bubble.id !== bubbleId));
            }
          });
        }
      });
    }
  };

  // Add floating animation to all bubbles
  useEffect(() => {
    bubbles.forEach(bubble => {
      const bubbleElement = document.getElementById(`bubble-${bubble.id}`);
      if (bubbleElement) {
        gsap.to(bubbleElement, {
          y: "+=10",
          duration: 2 + Math.random() * 2,
          repeat: -1,
          yoyo: true,
          ease: "power1.inOut",
          delay: Math.random() * 2
        });
      }
    });
  }, [bubbles]);

  return (
    <div 
      ref={containerRef}
      style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #f8f8f8 0%, #e8e8e8 100%)',
        position: 'relative',
        overflow: 'hidden',
        fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
      }}
    >
      {/* Minimal background pattern */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        background: `
          radial-gradient(circle at 20% 20%, rgba(0,0,0,0.02) 1px, transparent 1px),
          radial-gradient(circle at 60% 60%, rgba(0,0,0,0.02) 1px, transparent 1px),
          radial-gradient(circle at 80% 40%, rgba(0,0,0,0.02) 1px, transparent 1px)
        `,
        backgroundSize: '100px 100px, 150px 150px, 200px 200px'
      }} />

      {/* Input form in center */}
      <div style={{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        textAlign: 'center',
        zIndex: 10
      }}>
        <h1 style={{
          color: 'white',
          marginBottom: '30px',
          fontSize: '2.5rem',
          textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
          fontWeight: '300'
        }}>
          Create Bubbles
        </h1>

        <form onSubmit={handleSubmit} style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
          <input
            ref={inputRef}
            type="text"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            placeholder="Type something magical..."
            style={{
              padding: '15px 20px',
              fontSize: '16px',
              border: 'none',
              borderRadius: '25px',
              outline: 'none',
              minWidth: '300px',
              backgroundColor: 'rgba(255,255,255,0.9)',
              boxShadow: '0 4px 15px rgba(0,0,0,0.2)',
              transition: 'all 0.3s ease'
            }}
            onFocus={(e) => {
              e.target.style.backgroundColor = 'white';
              e.target.style.boxShadow = '0 6px 20px rgba(0,0,0,0.3)';
            }}
            onBlur={(e) => {
              e.target.style.backgroundColor = 'rgba(255,255,255,0.9)';
              e.target.style.boxShadow = '0 4px 15px rgba(0,0,0,0.2)';
            }}
          />
          <button
            type="submit"
            style={{
              padding: '15px 25px',
              fontSize: '16px',
              border: 'none',
              borderRadius: '25px',
              backgroundColor: '#FF6B6B',
              color: 'white',
              cursor: 'pointer',
              fontWeight: 'bold',
              boxShadow: '0 4px 15px rgba(0,0,0,0.2)',
              transition: 'all 0.3s ease'
            }}
            onMouseEnter={(e) => {
              e.target.style.backgroundColor = '#FF5252';
              e.target.style.transform = 'translateY(-2px)';
              e.target.style.boxShadow = '0 6px 20px rgba(0,0,0,0.3)';
            }}
            onMouseLeave={(e) => {
              e.target.style.backgroundColor = '#FF6B6B';
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 4px 15px rgba(0,0,0,0.2)';
            }}
          >
            Create Bubble
          </button>
        </form>

        <p style={{
          color: 'rgba(255,255,255,0.8)',
          marginTop: '20px',
          fontSize: '14px'
        }}>
          Click on bubbles to pop them! 🎈
        </p>
      </div>

      {/* Bubbles */}
      {bubbles.map(bubble => (
        <div
          key={bubble.id}
          id={`bubble-${bubble.id}`}
          onClick={() => popBubble(bubble.id)}
          style={{
            position: 'absolute',
            left: `${bubble.x}%`,
            top: `${bubble.y}%`,
            width: `${bubble.size}px`,
            height: `${bubble.size}px`,
            backgroundColor: bubble.color,
            borderRadius: '50%',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            cursor: 'pointer',
            boxShadow: '0 4px 15px rgba(0,0,0,0.2)',
            border: '2px solid rgba(255,255,255,0.3)',
            transition: 'filter 0.3s ease',
            zIndex: 5,
            userSelect: 'none'
          }}
          onMouseEnter={(e) => {
            e.target.style.filter = 'brightness(1.2)';
          }}
          onMouseLeave={(e) => {
            e.target.style.filter = 'brightness(1)';
          }}
        >
          <span style={{
            color: 'white',
            fontWeight: 'bold',
            fontSize: `${Math.max(12, bubble.size / 8)}px`,
            textAlign: 'center',
            textShadow: '1px 1px 2px rgba(0,0,0,0.3)',
            padding: '5px',
            wordBreak: 'break-word',
            lineHeight: '1.2'
          }}>
            {bubble.text}
          </span>
        </div>
      ))}

      {/* CSS Animation for background */}
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          50% { transform: translateY(-20px) rotate(180deg); }
        }
      `}</style>
    </div>
  );
};

export default BubblePage;
